ion = false","    until decision do","      puts \"----------\"","      puts \"主人公 HP:%i, <-> モンスター HP:%i\" % [player.health_point, monster.health_point]","      print \"ENTERキーを押下すると実行, 3を入力すると終了 > \"","      input = gets.chomp.to_i","      if (input == 3)","        exit","      end","      decision = battle.attack","    end","    battle.judges","  end","","end","","Game.new.start",""]}]}]]},"ace":{"folds":[],"scrolltop":428.5,"scrollleft":0,"selection":{"start":{"row":71,"column":15},"end":{"row":71,"column":15},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1429253057000,"hash":"a23ae8b8e4ef452f0b0bd19c5f76aaec973af1e3"}